import React, {useEffect,useRef} from 'react'
import {Button} from 'antd'
import Quill from 'quill'
import 'quill/dist/quill.snow.css'

export default function PostViewer() {
    const contentContainer = useRef(null)

    useEffect(() => { //React Hook- component needs to do something after render
        let article = document.createElement('article')
        new Quill(article)

        setTimeout(() => { contentContainer.current.appendChild(article) }, 0)
    },[])

    return (
        <main className="post-viewer">
            <Button type="primary">Edit Post</Button>
            <section ref={contentContainer}></section>
        </main>
    )
}
