import React from 'react'
import {PostMasonry, MasonryPost} from '../components/common'
import mygames from '../assets/mocks/mygames'
import gamecategories from '../assets/mocks/gamecategories'

const mygamesConfig = {
    1: {
        gridArea: '1 / 2 / 3 / 3'
    }
}

const categoriesConfig = {
    0: {
        gridArea: '1 / 1 / 2 / 3',
        height: '300px'
    },
    1: {
        height: '300px'
    },
    3: {
        height: '630px',
        marginLeft: '30px',
        width: '630px'
    }
}

const mergeStyles = function (posts, config) {
    posts.forEach((post, index) => {
        post.style = config[index]
    })
}

mergeStyles (mygames, mygamesConfig)
mergeStyles (gamecategories, categoriesConfig)

const lastPost = gamecategories.pop();

export default function Home() {
    return (
        <section className="container home">
            <div className="row">
                <h1>Game Categories</h1>
                <section className="categories-posts-container">
                    <PostMasonry posts={gamecategories} colums={2} tagsontop={true}/>
                    <MasonryPost post={lastPost} tagsOnTop={true} />
                </section>
                <h1>My Games</h1>
                <PostMasonry posts={mygames} columns={3} />
            </div>
        </section>
    )
}
