import React from 'react'
import {PostMasonry, MasonryPost, PostGrid} from '../components/common'
import mygames from '../assets/mocks/mygames'
import gamecategories from '../assets/mocks/gamecategories'

const mygamesConfig = {
    1: {
        gridArea: '1 / 2 / 3 / 3'
    }
}

const categoriesConfig = {
    0: {
        gridArea: '1 / 1 / 2 / 3',
        height: '300px'
    },
    1: {
        height: '300px'
    },
    3: {
        height: '630px',
        marginLeft: '30px',
        width: '630px'
    }
}

const mergeStyles = function (posts, config) {
    posts.forEach((post, index) => {
        post.style = config[index]
        post.author = 'Calvin Tang'
        post.description = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Donec eleifend porttitor tellus, non lobortis nisi semper vitae. Nam ut dolor suscipit, hendrerit turpis id, condimentum ante. Proin sed nisi tortor. Aenean mollis vitae risus et maximus. Nam vel ligula eget erat viverra interdum.'
    })
}

const recentPosts = [...gamecategories, ...mygames, ...mygames]

mergeStyles (mygames, mygamesConfig)
mergeStyles (gamecategories, categoriesConfig)

const lastPost = gamecategories.pop();

export default function Home() {
    return (
        <main className="home">
            <section className="container">
                <div className="row">
                    <section className="categories-posts-container">
                        <PostMasonry posts={gamecategories} colums={2} tagsontop={true}/>
                        <MasonryPost post={lastPost} tagsOnTop={true} />
                    </section>
                </div>
            </section>

            <section className="bg-white">
                <section className="container">
                    <div className="row">
                        <h1>Recent Posts</h1>
                        <PostGrid posts={recentPosts} />
                    </div>
                </section>
            </section>

            <section className="container">
                <div className="row">
                    <PostMasonry posts={mygames} columns={3} />
                </div>
            </section>
        </main>
    )
}
